// ColumnDisallowDropIndexAdvisor is the advisor checking for disallow CHANGE COLUMN statement.
type ColumnDisallowDropIndexAdvisor struct {
}

// Check checks for disallow CHANGE COLUMN statement.
func (*ColumnDisallowDropIndexAdvisor) Check(ctx advisor.Context, _ string) ([]advisor.Advice, error) {
	stmtList, ok := ctx.AST.([]ast.StmtNode)
	if !ok {
		return nil, errors.Errorf("failed to convert to StmtNode")
	}

	level, err := advisor.NewStatusBySQLReviewRuleLevel(ctx.Rule.Level)
	if err != nil {
		return nil, err
	}

	checker := &columnDisallowDropIndexChecker{
		level:   level,
		title:   string(ctx.Rule.Type),
		tables:  make(tableState),
		catalog: ctx.Catalog,
	}

	for _, stmt := range stmtList {
		checker.text = stmt.Text()
		checker.line = stmt.OriginTextPosition()
		(stmt).Accept(checker)
	}

	if len(checker.adviceList) == 0 {
		checker.adviceList = append(checker.adviceList, advisor.Advice{
			Status:  advisor.Success,
			Code:    advisor.Ok,
			Title:   "OK",
			Content: "",
		})
	}

	return checker.adviceList, nil
}

type columnDisallowDropIndexChecker struct {
	adviceList       []advisor.Advice
	level            advisor.Status
	title            string
	text             string
	indexColumnsName []string // 这里用表名+列名，因为列名可能重复
	tables           tableState
	line             int
	catalog          *catalog.Finder
}

func (checker *columnDisallowDropIndexChecker) Drop(in ast.Node) (ast.Node, bool) {
	if node, ok := in.(*ast.AlterTableStmt); ok {
		for _, spec := range node.Specs {
			if spec.Tp == ast.AlterTableDropColumn {
				table := node.Table.Name.O

				index := checker.catalog.Final.Index(&catalog.TableIndexFind{
					SchemaName: "",
					TableName:  table,
				})
				if index == nil {
					continue
				}
				indexColumnsName := []string{}
				for _, indexColumn := range *index {
					indexColumnsName = append(indexColumnsName, indexColumn.ExpressionList()...)
				}
				checker.indexColumnsName = append(checker.indexColumnsName, indexColumnsName...)

				colName := spec.OldColumnName.Name.String()
				if !checker.dropColumn(colName) {
					checker.adviceList = append(checker.adviceList, advisor.Advice{
						Status:  checker.level,
						Code:    advisor.DropIndexColumn,
						Title:   checker.title,
						Content: fmt.Sprintf("`%s`.`%s` cannot drop index column", table, colName),
						Line:    checker.line, // TODO
					})

				}
			}
		}
	}
	return in, false
}

// Enter implements the ast.Visitor interface.
func (checker *columnDisallowDropIndexChecker) Enter(in ast.Node) (ast.Node, bool) {
	switch node := in.(type) {
	case *ast.CreateTableStmt:
		if cnode, ok := in.(*ast.CreateTableStmt); ok {
			for _, spec := range cnode.Constraints {
				if spec.Tp == ast.ConstraintIndex {
					for _, key := range spec.Keys {
						checker.indexColumnsName = append(checker.indexColumnsName, key.Column.Name.O)
					}
				}
			}
		}
	case *ast.AlterTableStmt:
		return checker.Drop(node)
	}
	return in, false
}

func (checker *columnDisallowDropIndexChecker) dropColumn(column string) bool {
	for _, indexColumn := range checker.indexColumnsName {
		if indexColumn == column {
			return false
		}
	}
	return true
}

// Leave implements the ast.Visitor interface.
func (*columnDisallowDropIndexChecker) Leave(in ast.Node) (ast.Node, bool) {
	return in, true
}
