// StatementAffectedRowLimitAdvisor is the advisor checking for UPDATE/DELETE affected row limit.
type StatementAffectedRowLimitAdvisor struct {
}

// Check checks for UPDATE/DELETE affected row limit.
func (*StatementAffectedRowLimitAdvisor) Check(ctx context.Context, checkCtx advisor.Context) ([]*storepb.Advice, error) {
	stmtList, ok := checkCtx.AST.([]ast.Node)
	if !ok {
		return nil, errors.Errorf("failed to convert to Node")
	}

	level, err := advisor.NewStatusBySQLReviewRuleLevel(checkCtx.Rule.Level)
	if err != nil {
		return nil, err
	}
	payload, err := advisor.UnmarshalNumberTypeRulePayload(checkCtx.Rule.Payload)
	if err != nil {
		return nil, err
	}
	checker := &statementAffectedRowLimitChecker{
		level:                    level,
		title:                    string(checkCtx.Rule.Type),
		maxRow:                   payload.Number,
		ctx:                      ctx,
		driver:                   checkCtx.Driver,
		usePostgresDatabaseOwner: checkCtx.UsePostgresDatabaseOwner,
	}

	if payload.Number > 0 && checker.driver != nil {
		for _, stmt := range stmtList {
			checker.text = advisor.NormalizeStatement(stmt.Text())
			ast.Walk(checker, stmt)
			if checker.explainCount >= common.MaximumLintExplainSize {
				break
			}
		}
	}

	return checker.adviceList, nil
}

type statementAffectedRowLimitChecker struct {
	adviceList               []*storepb.Advice
	level                    storepb.Advice_Status
	text                     string
	title                    string
	maxRow                   int
	driver                   *sql.DB
	ctx                      context.Context
	explainCount             int
	setRoles                 []string
	usePostgresDatabaseOwner bool
}

// Visit implements ast.Visitor interface.
func (checker *statementAffectedRowLimitChecker) Visit(in ast.Node) ast.Visitor {
	code := advisor.Ok
	rows := int64(0)
	switch node := in.(type) {
	case *ast.VariableSetStmt:
		if node.Name == "role" {
			checker.setRoles = append(checker.setRoles, node.Text())
		}
	case *ast.UpdateStmt, *ast.DeleteStmt:
		checker.explainCount++
		res, err := advisor.Query(checker.ctx, advisor.QueryContext{
			UsePostgresDatabaseOwner: checker.usePostgresDatabaseOwner,
			PreExecutions:            checker.setRoles,
		}, checker.driver, storepb.Engine_POSTGRES, fmt.Sprintf("EXPLAIN %s", node.Text()))
		if err != nil {
			checker.adviceList = append(checker.adviceList, &storepb.Advice{
				Status:        checker.level,
				Code:          advisor.InsertTooManyRows.Int32(),
				Title:         checker.title,
				Content:       fmt.Sprintf("\"%s\" dry runs failed: %s", checker.text, err.Error()),
				StartPosition: common.ConvertPGParserLineToPosition(node.LastLine()),
			})
		} else {
			rowCount, err := getAffectedRows(res)
			if err != nil {
				checker.adviceList = append(checker.adviceList, &storepb.Advice{
					Status:        checker.level,
					Code:          advisor.Internal.Int32(),
					Title:         checker.title,
					Content:       fmt.Sprintf("failed to get row count for \"%s\": %s", checker.text, err.Error()),
					StartPosition: common.ConvertPGParserLineToPosition(node.LastLine()),
				})
			} else if rowCount > int64(checker.maxRow) {
				code = advisor.StatementAffectedRowExceedsLimit
				rows = rowCount
			}
		}
	}

	if code != advisor.Ok {
		checker.adviceList = append(checker.adviceList, &storepb.Advice{
			Status:        checker.level,
			Code:          code.Int32(),
			Title:         checker.title,
			Content:       fmt.Sprintf("The statement \"%s\" affected %d rows (estimated). The count exceeds %d.", checker.text, rows, checker.maxRow),
			StartPosition: common.ConvertPGParserLineToPosition(in.LastLine()),
		})
	}
	return checker
}
