import (
	"context"
	"database/sql"
	"fmt"

	"github.com/pkg/errors"

	"github.com/bytebase/bytebase/backend/common"
	storepb "github.com/bytebase/bytebase/backend/generated-go/store"
	"github.com/bytebase/bytebase/backend/plugin/advisor"
	"github.com/bytebase/bytebase/backend/plugin/parser/pg/legacy/ast"
)

var (
	_ advisor.Advisor = (*StatementDmlDryRunAdvisor)(nil)
	_ ast.Visitor     = (*statementDmlDryRunChecker)(nil)
)

func init() {
	advisor.Register(storepb.Engine_POSTGRES, advisor.SchemaRuleStatementDMLDryRun, &StatementDmlDryRunAdvisor{})
}

// StatementDmlDryRunAdvisor is the advisor checking for DML dry run.
type StatementDmlDryRunAdvisor struct {
}

// Check checks for DML dry run.
func (*StatementDmlDryRunAdvisor) Check(ctx context.Context, checkCtx advisor.Context) ([]*storepb.Advice, error) {
	stmtList, ok := checkCtx.AST.([]ast.Node)
	if !ok {
		return nil, errors.Errorf("failed to convert to Node")
	}

	level, err := advisor.NewStatusBySQLReviewRuleLevel(checkCtx.Rule.Level)
	if err != nil {
		return nil, err
	}
	checker := &statementDmlDryRunChecker{
		level:            level,
		title:            string(checkCtx.Rule.Type),
		driver:           checkCtx.Driver,
		ctx:              ctx,
		useDatabaseOwner: checkCtx.UsePostgresDatabaseOwner,
	}

	if checker.driver != nil {
		for _, stmt := range stmtList {
			ast.Walk(checker, stmt)
			if checker.explainCount >= common.MaximumLintExplainSize {
				break
			}
		}
	}

	return checker.adviceList, nil
}

type statementDmlDryRunChecker struct {
	adviceList       []*storepb.Advice
	level            storepb.Advice_Status
	title            string
	driver           *sql.DB
	ctx              context.Context
	explainCount     int
	setRoles         []string
	useDatabaseOwner bool
}

// Visit implements ast.Visitor interface.
func (checker *statementDmlDryRunChecker) Visit(in ast.Node) ast.Visitor {
	switch node := in.(type) {
	case *ast.VariableSetStmt:
		if node.Name == "role" {
			checker.setRoles = append(checker.setRoles, node.Text())
		}
	case *ast.InsertStmt, *ast.UpdateStmt, *ast.DeleteStmt:
		checker.explainCount++
		if _, err := advisor.Query(checker.ctx, advisor.QueryContext{
			UsePostgresDatabaseOwner: checker.useDatabaseOwner,
			PreExecutions:            checker.setRoles,
		}, checker.driver, storepb.Engine_POSTGRES, fmt.Sprintf("EXPLAIN %s", node.Text())); err != nil {
			checker.adviceList = append(checker.adviceList, &storepb.Advice{
				Status:        checker.level,
				Code:          advisor.StatementDMLDryRunFailed.Int32(),
				Title:         checker.title,
				Content:       fmt.Sprintf("\"%s\" dry runs failed: %s", node.Text(), err.Error()),
				StartPosition: common.ConvertPGParserLineToPosition(node.LastLine()),
			})
		}
	}

	return checker
}
