var (
	_ advisor.Advisor = (*StatementDisallowLimitAdvisor)(nil)
	_ ast.Visitor     = (*statementDisallowLimitChecker)(nil)
)

func init() {
	advisor.Register(storepb.Engine_TIDB, advisor.SchemaRuleStatementDisallowLimit, &StatementDisallowLimitAdvisor{})
}

// StatementDisallowLimitAdvisor is the advisor checking for no LIMIT clause in INSERT/UPDATE statement.
type StatementDisallowLimitAdvisor struct {
}

// Check checks for no LIMIT clause in INSERT/UPDATE statement.
func (*StatementDisallowLimitAdvisor) Check(_ context.Context, checkCtx advisor.Context) ([]*storepb.Advice, error) {
	stmtList, ok := checkCtx.AST.([]ast.StmtNode)
	if !ok {
		return nil, errors.Errorf("failed to convert to StmtNode")
	}

	level, err := advisor.NewStatusBySQLReviewRuleLevel(checkCtx.Rule.Level)
	if err != nil {
		return nil, err
	}
	checker := &statementDisallowLimitChecker{
		level: level,
		title: string(checkCtx.Rule.Type),
	}

	for _, stmt := range stmtList {
		checker.text = stmt.Text()
		checker.line = stmt.OriginTextPosition()
		(stmt).Accept(checker)
	}

	return checker.adviceList, nil
}

type statementDisallowLimitChecker struct {
	adviceList []*storepb.Advice
	level      storepb.Advice_Status
	title      string
	text       string
	line       int
}

// Enter implements the ast.Visitor interface.
func (checker *statementDisallowLimitChecker) Enter(in ast.Node) (ast.Node, bool) {
	code := advisorcode.Ok
	switch node := in.(type) {
	case *ast.UpdateStmt:
		if node.Limit != nil {
			code = advisorcode.UpdateUseLimit
		}
	case *ast.DeleteStmt:
		if node.Limit != nil {
			code = advisorcode.DeleteUseLimit
		}
	case *ast.InsertStmt:
		if useLimit(node) {
			code = advisorcode.InsertUseLimit
		}
	}

	if code != advisorcode.Ok {
		checker.adviceList = append(checker.adviceList, &storepb.Advice{
			Status:        checker.level,
			Code:          code.Int32(),
			Title:         checker.title,
			Content:       fmt.Sprintf("LIMIT clause is forbidden in INSERT, UPDATE and DELETE statement, but \"%s\" uses", checker.text),
			StartPosition: common.ConvertANTLRLineToPosition(checker.line),
		})
	}

	return in, false
}

// Leave implements the ast.Visitor interface.
func (*statementDisallowLimitChecker) Leave(in ast.Node) (ast.Node, bool) {
	return in, true
}

func useLimit(node *ast.InsertStmt) bool {
	if node.Select != nil {
		switch stmt := node.Select.(type) {
		case *ast.SelectStmt:
			return stmt.Limit != nil
		case *ast.SetOprStmt:
			return stmt.Limit != nil
		}
	}
	return false
}
