package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestColumnAutoIncrementInitialValue(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a int)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a int auto_increment) auto_increment = 1`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a int auto_increment) auto_increment = 2`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnInitialValueNotMatch,
					Title:   "column.auto-increment-initial-value",
					Content: "The initial auto-increment value in table `t` is 2, which doesn't equal 1",
					Line:    1,
				},
			},
		},
	}

	payload, err := json.Marshal(advisor.NumberTypeRulePayload{
		Number: 1,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &ColumnAutoIncrementInitialValueAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnAutoIncrementInitialValue,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
