func TestColumnAutoIncrementMustInteger(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a INT AUTO_INCREMENT)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a varchar(255) AUTO_INCREMENT)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnNotInteger,
					Title:   "column.auto-increment-must-integer",
					Content: "Auto-increment column `t`.`a` requires integer type",
					Line:    1,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(b int);
				ALTER TABLE t ADD COLUMN a varchar(255) AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnNotInteger,
					Title:   "column.auto-increment-must-integer",
					Content: "Auto-increment column `t`.`a` requires integer type",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(a int);
				ALTER TABLE t MODIFY COLUMN a varchar(255) AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnNotInteger,
					Title:   "column.auto-increment-must-integer",
					Content: "Auto-increment column `t`.`a` requires integer type",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(b int);
				ALTER TABLE t CHANGE COLUMN b a varchar(255) AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnNotInteger,
					Title:   "column.auto-increment-must-integer",
					Content: "Auto-increment column `t`.`a` requires integer type",
					Line:    3,
				},
			},
		},
	}

	advisor.RunSQLReviewRuleTests(t, tests, &ColumnAutoIncrementMustIntegerAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnAutoIncrementMustInteger,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: "",
	}, advisor.MockMySQLDatabase)
}
