func TestColumnAutoIncrementMustUnsigned(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a INT UNSIGNED AUTO_INCREMENT)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a INT AUTO_INCREMENT)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnSigned,
					Title:   "column.auto-increment-must-unsigned",
					Content: "Auto-increment column `t`.`a` is not UNSIGNED type",
					Line:    1,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(b int);
				ALTER TABLE t ADD COLUMN a INT AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnSigned,
					Title:   "column.auto-increment-must-unsigned",
					Content: "Auto-increment column `t`.`a` is not UNSIGNED type",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(a int);
				ALTER TABLE t MODIFY COLUMN a INT AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnSigned,
					Title:   "column.auto-increment-must-unsigned",
					Content: "Auto-increment column `t`.`a` is not UNSIGNED type",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(b int);
				ALTER TABLE t CHANGE COLUMN b a INT AUTO_INCREMENT`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.AutoIncrementColumnSigned,
					Title:   "column.auto-increment-must-unsigned",
					Content: "Auto-increment column `t`.`a` is not UNSIGNED type",
					Line:    3,
				},
			},
		},
	}

	advisor.RunSQLReviewRuleTests(t, tests, &ColumnAutoIncrementMustUnsignedAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnAutoIncrementMustUnsigned,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: "",
	}, advisor.MockMySQLDatabase)
}
