package mysql

// Framework code is generated by the generator.

import (
	"testing"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestColumnBlobTextDisallowDefault(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a BLOB DEFAULT NULL)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a BLOB DEFAULT '1')`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NotNullDefaultForBlobOrText,
					Title:   "column.disallow-blob-text-default",
					Content: "Try to set default value for BLOB or TEXT column `t`.`a`",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book ADD COLUMN a BLOB DEFAULT '1'`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NotNullDefaultForBlobOrText,
					Title:   "column.disallow-blob-text-default",
					Content: "Try to set default value for BLOB or TEXT column `tech_book`.`a`",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book CHANGE COLUMN name name BLOB DEFAULT '1'`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NotNullDefaultForBlobOrText,
					Title:   "column.disallow-blob-text-default",
					Content: "Try to set default value for BLOB or TEXT column `tech_book`.`name`",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book MODIFY COLUMN name BLOB DEFAULT '1'`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NotNullDefaultForBlobOrText,
					Title:   "column.disallow-blob-text-default",
					Content: "Try to set default value for BLOB or TEXT column `tech_book`.`name`",
					Line:    1,
				},
			},
		},
	}

	advisor.RunSQLReviewRuleTests(t, tests, &ColumnBlobTextDisallowDefaultAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnBlobTextDisallowDefault,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: "",
	}, advisor.MockMySQLDatabase)
}
