package mysql

// Framework code is generated by the generator.

import (
	"testing"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestColumnCommentRequire(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a int COMMENT 'some comments')`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(
				a int COMMENT 'some comments',
				b int,
				c int)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NoColumnComment,
					Title:   "column.comment-require",
					Content: "Column `t`.`b` requires comments",
					Line:    3,
				},
				{
					Status:  advisor.Warn,
					Code:    advisor.NoColumnComment,
					Title:   "column.comment-require",
					Content: "Column `t`.`c` requires comments",
					Line:    4,
				},
			},
		},
		{
			Statement: `ALTER TABLE t ADD COLUMN b int`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NoColumnComment,
					Title:   "column.comment-require",
					Content: "Column `t`.`b` requires comments",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE t CHANGE COLUMN a b int`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NoColumnComment,
					Title:   "column.comment-require",
					Content: "Column `t`.`b` requires comments",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE t MODIFY COLUMN b int`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NoColumnComment,
					Title:   "column.comment-require",
					Content: "Column `t`.`b` requires comments",
					Line:    1,
				},
			},
		},
	}

	advisor.RunSQLReviewRuleTests(t, tests, &ColumnCommentRequireAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnCommentRequirement,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: "",
	}, advisor.MockMySQLDatabase)
}
