package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestColumnDatetimeCountLimit(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a datetime)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a datetime, b datetime)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.DatetimeCountExceedsLimit,
					Title:   "column.datetime-count-limit",
					Content: "Table `t` has 2 datetime columns. The count greater than 1.",
					Line:    1,
				},
			},
		},
		{
			Statement: `
				ALTER TABLE tech_book ADD COLUMN a datetime;
				ALTER TABLE tech_book ADD COLUMN b datetime;
			`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.DatetimeCountExceedsLimit,
					Title:   "column.datetime-count-limit",
					Content: "Table `tech_book` has 2 datetime columns. The count greater than 1.",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				ALTER TABLE tech_book MODIFY COLUMN id datetime;
				ALTER TABLE tech_book MODIFY COLUMN name datetime;
			`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.DatetimeCountExceedsLimit,
					Title:   "column.datetime-count-limit",
					Content: "Table `tech_book` has 2 datetime columns. The count greater than 1.",
					Line:    3,
				},
			},
		},
	}

	payload, err := json.Marshal(advisor.NumberTypeRulePayload{
		Number: 1,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &ColumnDatetimeCountLimitAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleDatetimeColumnCountLimit,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
