package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestColumnMaximumCharacterLength(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(name char(20));`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(name varchar(225));`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(name char(225));`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.CharLengthExceedsLimit,
					Title:   "column.maximum-character-length",
					Content: "The length of the CHAR column `name` is bigger than 20, please use VARCHAR instead",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book ADD COLUMN name_2 char(225)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.CharLengthExceedsLimit,
					Title:   "column.maximum-character-length",
					Content: "The length of the CHAR column `name_2` is bigger than 20, please use VARCHAR instead",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book CHANGE COLUMN name name char(225)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.CharLengthExceedsLimit,
					Title:   "column.maximum-character-length",
					Content: "The length of the CHAR column `name` is bigger than 20, please use VARCHAR instead",
					Line:    1,
				},
			},
		},
		{
			Statement: `ALTER TABLE tech_book MODIFY COLUMN name char(225)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.CharLengthExceedsLimit,
					Title:   "column.maximum-character-length",
					Content: "The length of the CHAR column `name` is bigger than 20, please use VARCHAR instead",
					Line:    1,
				},
			},
		},
	}

	payload, err := json.Marshal(advisor.NumberTypeRulePayload{
		Number: 20,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &ColumnMaximumCharacterLengthAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleColumnMaximumCharacterLength,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
