package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestIndexKeyNumberLimit(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a int, b int, primary key (a, b))`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(
				a int,
				b int,
				primary key pk (a, b, b, b, b, b, b))`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.IndexKeyNumberExceedsLimit,
					Title:   "index.key-number-limit",
					Content: "The number of index `pk` in table `t` should be not greater than 5",
					Line:    4,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(a int, b int, c int, d int, e int, f int, g int);
				CREATE INDEX idx ON t(a, b, c, d, e, f, g)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.IndexKeyNumberExceedsLimit,
					Title:   "index.key-number-limit",
					Content: "The number of index `idx` in table `t` should be not greater than 5",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(a int, b int, c int, d int, e int, f int, g int);
				ALTER TABLE t ADD UNIQUE KEY uk (a, b, c, d, e, f, g)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.IndexKeyNumberExceedsLimit,
					Title:   "index.key-number-limit",
					Content: "The number of index `uk` in table `t` should be not greater than 5",
					Line:    3,
				},
			},
		},
		{
			Statement: `
				CREATE TABLE t(a int, b int, c int, d int, e int, f int, g int);
				ALTER TABLE t ADD FOREIGN KEY uk (a, b, c, d, e, f, g) REFERENCES t1(a, b, c, d, e, f, g)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.IndexKeyNumberExceedsLimit,
					Title:   "index.key-number-limit",
					Content: "The number of index `uk` in table `t` should be not greater than 5",
					Line:    3,
				},
			},
		},
	}

	payload, err := json.Marshal(advisor.NumberLimitRulePayload{
		Number: 5,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &IndexKeyNumberLimitAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleIndexKeyNumberLimit,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
