package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestInsertRowLimit(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `INSERT INTO tech_book values(1, 'a')`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `INSERT INTO tech_book values(1, 'a'), (2, 'b'), (3, 'c'), (4, 'd'), (5, 'e'), (6, 'f')`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.InsertTooManyRows,
					Title:   "statement.insert.row-limit",
					Content: "\"INSERT INTO tech_book values(1, 'a'), (2, 'b'), (3, 'c'), (4, 'd'), (5, 'e'), (6, 'f')\" inserts 6 rows. The count exceeds 5.",
					Line:    1,
				},
			},
		},
		{
			Statement: `INSERT INTO tech_book SELECT * FROM tech_book`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
	}
	payload, err := json.Marshal(advisor.NumberTypeRulePayload{
		Number: 5,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &InsertRowLimitAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleStatementInsertRowLimit,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
