package mysql

// Framework code is generated by the generator.

import (
	"testing"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestStatementDisallowMultiTransaction(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `
			START TRANSACTION;
			CREATE TABLE techBook(id int, name varchar(255));
			COMMIT;
			`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `
			START TRANSACTION;
			CREATE TABLE techBook(id int, name varchar(255));
			COMMIT;

			START TRANSACTION;
			INSERT INTO techBook(id, name) VALUES(1, "book");
			COMMIT;
			`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.StatementDisallowMultiTransaction,
					Title:   "statement.disallow-multi-transaction",
					Content: "Multiply transaction is not allowed, related statement: \"\n\t\t\tSTART TRANSACTION;\"",
					Line:    6,
				},
			},
		},
	}

	advisor.RunSQLReviewRuleTests(t, tests, &StatementDisallowMultiTransactionAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleStatementDisallowMultiTransaction,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: "",
	}, advisor.MockMySQLDatabase)
}
