package mysql

// Framework code is generated by the generator.

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/bytebase/bytebase/plugin/advisor"
)

func TestTableCommentConvention(t *testing.T) {
	tests := []advisor.TestCase{
		{
			Statement: `CREATE TABLE t(a int) COMMENT 'some comments'`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Success,
					Code:    advisor.Ok,
					Title:   "OK",
					Content: "",
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a int)`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.NoTableComment,
					Title:   "table.comment",
					Content: "Table `t` requires comments",
					Line:    1,
				},
			},
		},
		{
			Statement: `CREATE TABLE t(a int) COMMENT 'sdlfkjalkseblkjduafelbnlsdfkljayue'`,
			Want: []advisor.Advice{
				{
					Status:  advisor.Warn,
					Code:    advisor.TableCommentTooLong,
					Title:   "table.comment",
					Content: "The length of table `t` comment should be within 20 characters",
					Line:    1,
				},
			},
		},
	}

	payload, err := json.Marshal(advisor.CommentConventionRulePayload{
		Required:  true,
		MaxLength: 20,
	})
	require.NoError(t, err)
	advisor.RunSQLReviewRuleTests(t, tests, &TableCommentConventionAdvisor{}, &advisor.SQLReviewRule{
		Type:    advisor.SchemaRuleTableCommentConvention,
		Level:   advisor.SchemaRuleLevelWarning,
		Payload: string(payload),
	}, advisor.MockMySQLDatabase)
}
